import React from 'react';

const Product = ({ product, onAddToCart }) => {
  return (
    <div>
      <img src={`path_to_image/${product.name}.jpg`} alt={product.name} />
      <h2>{product.name}</h2>
      <p>${product.price}</p>
      <button onClick={() => onAddToCart(product)}>Add to Cart</button>
    </div>
  );
}

export default Product;
