import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import Header from '../components/header'; // Asegúrate de que la ruta es correcta
import CartItem from '../components/cartitem'; // Asegúrate de que la ruta es correcta

const CartPage = () => {
  const cart = useSelector(state => state.cart.cart);

  // Calcula el costo total y el número total de ítems en el carrito
  const totalCost = cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  const totalItems = cart.reduce((total, item) => total + item.quantity, 0);

  return (
    <div>
      <Header />
      <h2>Your Cart</h2>
      <div className="cart-list">
        {cart.map(item => (
          <CartItem key={item.id} item={item} />
        ))}
      </div>
      <p>Total Plantas: {totalItems}</p>
      <p>Total costo: ${totalCost.toFixed(2)}</p>
      <button>Checkout (Esperando)</button>
      <Link to="/">Continua comprando</Link>
    </div>
  );
};

export default CartPage;
