import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import Header from '../components/header'; // Asegúrate de que la ruta es correcta
import CartItem from '../components/cartitem'; // Asegúrate de que la ruta es correcta

const CartPage = () => {
  const carrito = useSelector(state => state.cart.cart);

  // Calcula el costo total y el número total de ítems en el carrito
  const costoTotal = carrito.reduce((total, item) => total + (item.price * item.quantity), 0);
  const totalItems = carrito.reduce((total, item) => total + item.quantity, 0);

  return (
    <div>
      <Header />
      <h2>Tu Carrito</h2>
      <div className="cart-list">
        {carrito.map(item => (
          <CartItem key={item.id} item={item} />
        ))}
      </div>
      <p>Total de Plantas: {totalItems}</p>
      <p>Costo Total: ${costoTotal.toFixed(2)}</p>
      <button>Checkout (Próximamente)</button>
      <Link to="/">Continuar Comprando</Link>
    </div>
  );
};

export default CartPage;
